mapper.select:
select id, content, depth, member_id
from reply
where id = #{id}

select id
from reply
where post_id = #{id}

mapper.update:
update reply
set content = #{content}, modified_date = sysdate
where id = #{id}

update reply
set modified_date = sysdate, removed = 1
where id = #{id}

update reply
set modified_date = sysdate, removed = 1
where id = #{id} or parent_id = #{id}
